 */
import React from "react";
import { ScrollView, StyleSheet, Text, View } from "react-native";

import type { CodeFrame } from "../Data/parseLogBoxLog";
import { Ansi } from "../UI/AnsiHighlight";
import { LogBoxButton } from "../UI/LogBoxButton";
import * as LogBoxStyle from "../UI/LogBoxStyle";
import { CODE_FONT } from "../UI/constants";
import { formatProjectFilePath } from "../formatProjectFilePath";
import openFileInEditor from "../modules/openFileInEditor";
import { LogBoxInspectorSection } from "./LogBoxInspectorSection";

declare const process: any;

export function LogBoxInspectorCodeFrame({
  codeFrame,
}: {
  codeFrame?: CodeFrame;
}) {
  if (codeFrame == null) {
    return null;
  }

  function getFileName() {
    return formatProjectFilePath(
      process.env.EXPO_PROJECT_ROOT,
      codeFrame?.fileName
    );
  }

  function getLocation() {
    const location = codeFrame?.location;
    if (location != null) {
      return ` (${location.row}:${
        location.column + 1 /* Code frame columns are zero indexed */
      })`;
    }

    return null;
  }

  return (
    <LogBoxInspectorSection heading="Source">
      <View style={styles.box}>
        <View style={styles.frame}>
          <ScrollView horizontal>
            <Ansi style={styles.content} text={codeFrame.content} />
          </ScrollView>
        </View>
        <LogBoxButton
          backgroundColor={{
            default: "transparent",
            pressed: LogBoxStyle.getBackgroundDarkColor(1),
          }}
          style={styles.button}
          onPress={() => {
            openFileInEditor(codeFrame.fileName, codeFrame.location?.row ?? 0);
          }}
        >
          <Text style={styles.fileText}>
            {getFileName()}
            {getLocation()}
          </Text>
        </LogBoxButton>
      </View>
    </LogBoxInspectorSection>
  );
}

const styles = StyleSheet.create({
  box: {
    backgroundColor: LogBoxStyle.getBackgroundColor(),
    borderWidth: 1,
    borderColor: "#323232",
    marginLeft: 10,
    marginRight: 10,
    marginTop: 5,
    borderRadius: 3,
  },
  frame: {
    padding: 10,
    borderBottomColor: LogBoxStyle.getTextColor(0.1),
    borderBottomWidth: 1,
  },
  button: {
    paddingTop: 10,
    paddingBottom: 10,
  },
  content: {
    color: LogBoxStyle.getTextColor(1),
    fontSize: 12,
    includeFontPadding: false,
    lineHeight: 20,
    fontFamily: CODE_FONT,
  },
  fileText: {
    userSelect: "none",
    color: LogBoxStyle.getTextColor(0.5),
    textAlign: "center",
    flex: 1,
    fontSize: 16,
    includeFontPadding: false,
    fontFamily: CODE_FONT,
  },
});
